package com.moengage.capacitor

import com.moengage.capacitor.internal.MODULE_TAG
import com.moengage.core.internal.inapp.InAppManager
import com.moengage.core.internal.logger.Logger
import com.moengage.inapp.MoEInAppHelper

/**
 * Helper class for MoEngage native SDK's API access
 * @author Arshiya Khanum
 * @since 1.0.0
 */
public object MoECapacitorHelper {

    private const val tag = "${MODULE_TAG}MoECapacitorHelper"

    /**
     * Notify SDK when screen orientation changes, for SDK to handle in-app display.
     * NOTE: Use this API only when your Activity handles the screen orientation change by itself.
     * @since 1.0.0
     */
    public fun onConfigurationChanged() {
        Logger.print { "$tag onConfigurationChanged() : " }
        if (!InAppManager.hasModule()) {
            Logger.print { "$tag onConfigurationChanged() : InApp module not found." }
            return
        }
        MoEInAppHelper.getInstance().onConfigurationChanged()
    }
}